import { useState } from "react";
const ToggleMode = () =>{
    const [isOn, setIsOn] = useState(false);

    const toggle = () =>{
        setIsOn(!isOn);
    }

    return(
        <div>
            <button onClick={toggle}>
                {isOn ? "Light Mode" : "Dark Mode"}
            </button>
        </div>
    )
}

export default ToggleMode;
